import React, { useState, useEffect } from "react"
const Converter = () => {
  const [id, setId] = useState("");
  const [currencies, setCurrencies] = useState(["Bitcoin", "Ethereum", "Dogecoin", "XRP"]);


  const fetchConversions = async () => {
    try {
      console.log(id);
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=eth,ltc,xrp`);
      const json = await response.json();
      console.log(json);
    } catch (err) {
      console.log(err);
    }
  };

  const getValue = (value) => {
    setId(value);
    fetchConversions();
  }

  return(
    <div>
      <ul>
        <li onClick={(e) => getValue(e.target.innerText)}>Bitcoin</li>
        <li onClick={(e) => getValue(e.target.innerText)}>Ethereum</li>
        <li onClick={(e) => getValue(e.target.innerText)}>Litecoin</li>
        <li onClick={(e) => getValue(e.target.innerText)}>XRP</li>
      </ul>
    </div>
  )
}

export default Converter
