import React, { useState, useEffect } from "react"
const Converter = () => {
  const [response, setResponse] = useState([]);
  const [parsedResults, setParsedResults] = useState([]);


  const fetchConversions = async (value) => {
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${value}&vs_currencies=btc,eth,ltc,xrp`);
      const json = await response.json();
      parseData(json);
      console.log(json);
    } catch (err) {
      console.log(err);
    }
  };

  const parseData = (obj) => {

    const values = Object.values(obj)
    values.forEach(val => {
      if ( val && typeof val === 'object' ) {
        // object found continue recursion
        parseData(val);
      } else {
        const clonedArray = [...parsedResults]
        clonedArray.push(val)
        setParsedResults(clonedArray)
      }
    })
    console.log(clonedArray);
    setParsedResults(clonedArray);
  }

  return(
    <div>
      <ul>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Bitcoin</li>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Ethereum</li>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Litecoin</li>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Ripple</li>
      </ul>

      <div>
        {
          parsedResults.length > 0 ? (parsedResults.map((result, index) => {
            return (<span key={index}>{result}</span>)
          })) : <div>Select a currency</div>

        }
      </div>

    </div>
  )
}

export default Converter
