const Converter = () => {
  const [response, setResponse] = useState([]);


  const fetchConversions = async (value) => {
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${value}&vs_currencies=btc,eth,ltc,xrp`);
      const json = await response.json();
      parseValues(json);
      console.log(json);
    } catch (err) {
      console.log(err);
    }
  };

  const parseValues = (obj) => {
    const baseCurrency = Object.keys(obj)[0];
    const conversions = obj[baseCurrency];

  }

  return(
    <div>
      <ul>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Bitcoin</li>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Ethereum</li>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Litecoin</li>
        <li onClick={(e) => fetchConversions(e.target.innerText)}>Ripple</li>
      </ul>

      <div>
        {
          // parsedResults.length > 0 ? (parsedResults.map((result, index) => {
          //   return (<span key={index}>{index + ": " + result}</span>)
          // })) : <div>Select a currency</div>
        }
      </div>

    </div>
  )
}

export default Converter
